import React, { useEffect, useState } from 'react'
import axios from 'axios'
import './ViewClosed.css'
import * as ReactBootStrap from 'react-bootstrap';
export default function ViewClosed(props) {
    const [tickets ,setTickets]=useState([])
    const [details, setDetail]= useState([])

   useEffect(()=> {
        axios.get('http://localhost:8080/bus/ticket/viewClosed').then((res)=>{
        setTickets(res.data);
         
        },[]);
    })
        const renderTicket = (ticket,index) => {   
                    
              
            axios.get(`http://localhost:8080/bus/pass/${ticket._id.toString()}`).then((res)=>{
                    setDetail(res.data);
                   console.log(details)    
                },[])    
            return(
                
                <button  className="btn btn-info hov " key={index} > 
                    <td style={{backgroundColor:"darkblue", borderColor:"darkblue",width:"40px"}}>{ticket.seatID}</td>
                </button>
                
            )           
        }
        

    return (
        <div className="ss">
            <div className="row">
                <div className="columnb">
                    <div className="plane">
                        {/* <form >
                           
                        </form> */}
                    </div>
                </div>
                <div className="column2 ">
                    <div className="seatInfo  ">
                        
                        <div>
                            <button  style={{marginLeft: "0px",width:"100%",height:"60px"}} className="btn  btn-info  seatBT">
                              View Opened Tickets 
                            </button>
                           <button tittle={details.name} class="hov">

                           </button>
                            <br></br>
                            <ReactBootStrap.Table  >
                              <br></br>   
                             <tbody class="info" > 
                                                      
                            <span class="extra-info ">
                            <ul >
                            <li>Name:{details.name}</li>
                            <li>Age : { details.age}</li>
                           
                            <li>Email ID:{ details.email}</li>
                        </ul>  
                       
                            </span>
                            {tickets.map(renderTicket)}
                            </tbody> 
                            <a href='/Admin/pass/'>                         
                            {/* <button  style={{marginLeft: "0px",width:"100%",height:"60px"}} className="btn btn-info seatBT">
                              View pass
                            </button> */}
                            </a>
                           
                            
 
                             </ReactBootStrap.Table>
  
                             </div>                     
                        </div>
                   </div>
             </div>
        </div>
    );
}
