    // const [reservedSeat, setReservedSeat] = useState([])
    // const [seatNumber, setSeatnumber] = useState([])
    
  
    
    const handleSubmitDetails = () => {
       
        
        axios.get('http://localhost:8080/bus/ticket/viewClosed').then((res)=>{
            //console.log(res.data);
          if(res.length === 0) return null;
           
            setTickets(res.data);
               
            
            
           
            // eslint-disable-next-line array-callback-return
        //     return tickets.map((ticket,index) => {
        //                         <div key={index}>
        //                             <h1 >{ticket.seatID}</h1>
                                 
        //                         </div>
                               
        //                         console.log(ticket.seatID);
                                    
                              
        //                     })
        // })
        })
    }

    const renderTicket = (ticket) => {   
        
           

            // axios.get(`http://localhost:8080/bus/pass/${id.toString()}`).then((res)=>{
         
              // console.log(ticket._id);
                // console.log(res.data);
                
              setDetail(ticket._id)
                console.log(ticket._id);
               // console.log(res.data);
               
                
                
            
          
        return(
            <tr className="btn btn-info " key={index}> 
                <td style={{backgroundColor:"darkblue", borderColor:"darkblue",width:"40px"}}>{ticket.seatID}</td>
                {setDetail(ticket._id)}
            </tr>
        )       
    }
        
        const renDetails = (detail) => {    
           
            return(
                <tr className=" info seat-info" > 
                    <td style={{backgroundColor:"darkblue", borderColor:"darkblue",width:"40px"}}>{detail.name} {detail.age} { detail.email}</td>
                    {/* {
                    axios.get(`http://localhost:8080/bus/pass/${ticket.passengerObj}`).then((resp)=>{
                        setDetail(resp.data);
                    })
                } */}
        
                </tr>
               
            )    
            
        }
   
    
        //    // console.log(response);
        //     if(response.data===true){
        //       window.location.href='/';
        //     }
        //     else{
        //       window.location.href='/routes';
        //     }
            
            
        //     }).catch(function (error) {
            
        //     console.log(error);
            
        //     });
            
    // const  displayClosed = (tickets)=> {
    //     if(!tickets.length) return null;

    //            return tickets.map((ticket,index)=> {
    //                <div key={index}>
    //                     <h3>{ticket.seatID}</h3>
                       
    //                 </div>
                       
    //                 console.log(ticket.seatID);
                
    //             })
    // }
    

       

    return (
        <div className="ss">
            <div className="row">
                <div className="columna">
                    <div className="plane">
                        {/* <form >
                        
                        </form> */}
                    </div>
                </div>
                <div className="column2">
                    <div className="seatInfo">
                        
                        <div>
                       
                            <button onClick={ handleSubmitDetails()} style={{marginLeft: "0px",width:"100%",height:"60px"}} className="btn btn-info seatBT"> 
                              View Closed Passengers
                            </button>
                           
                            <ReactBootStrap.Table  >
                               
                                <tr > <br></br></tr>
                               <tbody class="info"> <span class="extra-info">
                                   
                       <ul >
                          
                            <li>Name:{details.name}</li>
                            <li>Age : { details.age}</li>
                           
                            <li>Email ID:{ details.email}</li>
                        </ul>   
                            </span>
                     {console.log(details.name)}
                            {tickets.map(renderTicket)}
                            </tbody>
 
                             </ReactBootStrap.Table>
                        
                            
                       
                    </div>
                      
                </div>
            </div>
                    </div>
        </div>
    );
}
