import React from "react";

import './PaymentTab.css'
//import jwt_decode from 'jwt-decode'

import "react-credit-cards/es/styles-compiled.css";

export default class App extends React.Component {

    state = {

        token: "",
    }

    componentDidMount() {
        // const tok = sessionStorage.getItem("authToken")
        // const decoded = jwt_decode(tok)
        // this.setState({ token: decoded.user })
    }

    handleCallback = ({ issuer }, isValid) => {
        if (isValid) {
            this.setState({ issuer });
        }
    };

    handleInputFocus = ({ target }) => {
        this.setState({
            focused: target.name
        });
    };

    // handleInputChange = ({ target }) => {
    //     if (target.name === "number") {
    //         target.value = formatCreditCardNumber(target.value);
    //     } else if (target.name === "expiry") {
    //         target.value = formatExpirationDate(target.value);
    //     } else if (target.name === "cvc") {
    //         target.value = formatCVC(target.value);
    //     }

    //     this.setState({ [target.name]: target.value });
    // };

    handleSubmit = e => {
        e.preventDefault();
        const formData = [...e.target.elements]
            .filter(d => d.name)
            .reduce((acc, d) => {
                acc[d.name] = d.value;
                return acc;
            }, {});

        this.setState({ formData });
        this.form.reset();
    };

    // moveToTicketPage = (e) => {
    //     e.preventDefault()
    //     localStorage.setItem("paymentData", JSON.stringify(this.state.token))
    //     window.location.href = "/getTicket"
    // }

    renderNamesOfPassenger = () => {
        let passArray = localStorage.getItem('nameData')
        if (passArray) {
            let nameArray = JSON.parse(passArray)
            return nameArray.map((name, idx) => {
                return (
                    <p key={idx}>{name}</p>
                )
            })
        }
    }

    renderSeatNumbers = () => {
        let seatArray = localStorage.getItem('reservedSeats')
        if (seatArray) {
            let seaArr = JSON.parse(seatArray)
            return seaArr.map((seat, idx) => {
                return (
                    <p key={idx}>{seat}</p>
                )
            })
        }
    }



    render() {
        const { token } = this.state;

        return (

            <div className="paym">
                <div className="row">





                    <div className="columnTwo">
                        <h3>BUS BOOK</h3>
                        <div>
                            <p>BOOKING DETAILS</p>
                            <div className="row">
                                <div className="col-6 pt">
                                    <p className="hdng">Username</p>
                                    <hr className="hr3" />


                                    <p className="hdng">Passengers</p>
                                    {this.renderNamesOfPassenger()}
                                    <hr className="hr3" />


                                </div>
                                <div className="col-6">
                                    <p className="usrName">{token.name}</p>
                                    <hr className="hr3" />


                                    <p className="hdng">Seat No</p>
                                    {this.renderSeatNumbers()}
                                    <hr className="hr3" />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>


        );
    }
}
