import React, { useState } from 'react'
import axios from 'axios'
//import { FaAngleDoubleDown } from "react-icons/fa";
import './PassPage.css'
import * as ReactBootStrap from 'react-bootstrap';
export default function PassPage() {
    const [tickets ,setTickets]=useState([])
    const [details, setDetail]=useState([])
    
    const handleSubmitDetails = () => {
        axios.get('http://localhost:8080/bus/ticket/viewOpen').then((res)=>{
           // console.log(res.data);
           
            setTickets(res.data);
           
           
            // eslint-disable-next-line array-callback-return
        //     return tickets.map((ticket,index) => {
        //                         <div key={index}>
        //                             <h1 >{ticket.seatID}</h1>
        //                         </div>                             
        //                         console.log(ticket.seatID);                          
        //                     })
        // })
        });
    }
        const renderTicket = (ticket) => {          
                
                
               // const getdetail=`http://localhost:8080/bus/pass/${id.toString()}`;
            setDetail(ticket._id);
            console.log(details)
              
                    // console.log(res.data);
                    
                   
                    //console.log(resp.data);
            
               // console.log(details);
               // eslint-disable-next-line no-lone-blocks
               {
                <tr className="btn btn-info " key={ticket.seatID} > 
                    <td style={{backgroundColor:"darkblue", borderColor:"darkblue",width:"40px"}}>{ticket.seatID}</td>
                    {/* {
                    axios.get(`http://localhost:8080/bus/pass/${ticket.passengerObj}`).then((resp)=>{
                        setDetail(resp.data);
                    })
                } */}
                </tr>
               }
                
                      
        }
        const renderDetails = (detail,index) => {          
            return(
                <tr className="btn btn-info " key={index}> 
                    <td style={{backgroundColor:"darkblue", borderColor:"darkblue",width:"40px"}}></td>
                    {/* {
                    axios.get(`http://localhost:8080/bus/pass/${ticket.passengerObj}`).then((resp)=>{
                        setDetail(resp.data);
                    })
                } */}
                </tr>
            )           
        }
        
               // console.log(details);
                    
                
                      
        
        // const renderDetails = (detail,index) => {          
        //     return(
        //         <span className="info " style={{backgroundColor:"darkblue",width:"40px"}} key={index}> 
        //             {/* <td style={{backgroundColor:"darkblue", borderColor:"darkblue",width:"40px"}}> */}
                   
        //             <ul>
        //                     <li>Name:{detail.name}</li>
                           
        //                 </ul> 
                    
                
        //         </span>
        //     )           
        // }
        //    // console.log(response);
        //     if(response.data===true){
        //       window.location.href='/';
        //     }
        //     else{
        //       window.location.href='/routes';
        //     }    
        //     }).catch(function (error) {
            
        //     console.log(error);           
        //     });           
    // const  displayClosed = (tickets)=> {
    //     if(!tickets.length) return null;

    //            return tickets.map((ticket,index)=> {
    //                <div key={index}>
    //                     <h3>{ticket.seatID}</h3>                    
    //                 </div>
                       
    //                 console.log(ticket.seatID);
                
    //             })
    // }

    return (
        <div className="ss">
            <div className="row">
                <div className="columnb">
                    <div className="plane">
                        {/* <form >
                           
                        </form> */}
                    </div>
                </div>
                <div className="column2">
                    <div className="seatInfo">
                        
                        <div>
                            <button onClick={ handleSubmitDetails()}  style={{marginLeft: "0px",width:"100%",height:"60px"}} className="btn btn-info seatBT">
                              View Opened Tickets
                            </button>
                            
                              
                             
                            <ReactBootStrap.Table  >
                                <br></br>
                            <tbody >
                            {tickets.map(renderTicket)}
                           
                        <ul  >
                       
                            <li>Name:{details.name}</li>
                            <li>Seat ID</li>
                        </ul>   
                            
                           
                           
                            </tbody>
                                     
                                     
                               
                             </ReactBootStrap.Table>
                            
                       
                    </div>
                      
                </div>
            </div>
                    </div>
        </div>
    );
}
