import React, { useState } from 'react'
import axios from 'axios'
//import { FaAngleDoubleDown } from "react-icons/fa";
import './ViewClosed.css'
import * as ReactBootStrap from 'react-bootstrap';
export default function ViewClosed() {
    const [tickets ,setTickets]=useState([])
    const [details, setDetail]= useState([])
    //const [erro,setError]=useState([])
    //const[seatId, setId]=useState([])
    //const [reservedSeat, setReservedSeat] = useState([])

    // eslint-disable-next-line react/no-direct-mutation-state
    
    // const [name, setName] = useState([])
    
    // const [email, setEmail] = useState([])
    // const [phone, setPhone] = useState([])
    // const [age, setAge] = useState([])
    // const [arrowDown, setArrowDown] = useState(false)
    // const [gender, setGender] = useState([])
    // const [reservedSeat, setReservedSeat] = useState([])
    // const [seatNumber, setSeatnumber] = useState([])
    

    const handleSubmitDetails = () => {
        axios.get('http://localhost:8080/bus/ticket/viewClosed').then((res)=>{
           // console.log(res.data);
            setTickets(res.data);
            
           
            // eslint-disable-next-line array-callback-return
        //     return tickets.map((ticket,index) => {
        //                         <div key={index}>
        //                             <h1 >{ticket.seatID}</h1>
        //                         </div>                             
        //                         console.log(ticket.seatID);                          
        //                     })
        // })
        });
    }
        const renderTicket = (ticket,index) => {   
            var id= ticket._id;
                
                //const getdetail=`http://localhost:8080/bus/pass/${id.toString()}`;

                axios.get(`http://localhost:8080/bus/pass/${id.toString()}`).then((res)=>{
                   
                    // console.log(res.data);
                    
                   
                    setDetail(res.data);
                    
                    
                    
                })    
            return(
                <tr className="btn btn-info " key={index} > 
                    <td style={{backgroundColor:"darkblue", borderColor:"darkblue",width:"40px"}}>{ticket.seatID}</td>
                    >td
                    {/* {
                    axios.get(`http://localhost:8080/bus/pass/${ticket.passengerObj}`).then((resp)=>{
                        setDetail(resp.data);
                    })
                } */}
                </tr>
            )           
        }
        // const renDetails = (detail) => {    
           
        //     return(
        //         <tr className="btn btn-info " > 
        //             <td style={{backgroundColor:"darkblue", borderColor:"darkblue",width:"40px"}}></td>
        //             {/* {
        //             axios.get(`http://localhost:8080/bus/pass/${ticket.passengerObj}`).then((resp)=>{
        //                 setDetail(resp.data);
        //             })
        //         } */}
       
        //         </tr>
               
        //     )    
            
        // }
        // const renderDetails = (detail,index) => {          
        //     return(
        //         <span className="info " style={{backgroundColor:"darkblue",width:"40px"}} key={index}> 
        //             {/* <td style={{backgroundColor:"darkblue", borderColor:"darkblue",width:"40px"}}> */}
                   
        //             <ul>
        //                     <li>Name:{detail.name}</li>
                           
        //                 </ul> 
                    
                
        //         </span>
        //     )           
        // }
        //    // console.log(response);
        //     if(response.data===true){
        //       window.location.href='/';
        //     }
        //     else{
        //       window.location.href='/routes';
        //     }    
        //     }).catch(function (error) {
            
        //     console.log(error);           
        //     });           
    // const  displayClosed = (tickets)=> {
    //     if(!tickets.length) return null;

    //            return tickets.map((ticket,index)=> {
    //                <div key={index}>
    //                     <h3>{ticket.seatID}</h3>                    
    //                 </div>
                       
    //                 console.log(ticket.seatID);
                
    //             })
    // }

    return (
        <div className="ss">
            <div className="row">
                <div className="columna">
                    <div className="plane">
                        {/* <form >
                           
                        </form> */}
                    </div>
                </div>
                <div className="column2">
                    <div className="seatInfo">
                        
                        <div>
                            <a href='/Admin/pass'>
                            <button onClick={ handleSubmitDetails()}  style={{marginLeft: "0px",width:"100%",height:"60px"}} className="btn btn-info seatBT">
                              View Closed Tickets
                            </button>
                            </a>
                              
                             
                            <ReactBootStrap.Table  >
                              <br></br> 
                            <tbody class="info"> <span class="extra-info">
                            
                            
                           
                        <ul >
                            <li className="name">Name:{details.name}</li>
                            <li>Age : { details.age}</li>
                           
                            <li>Email ID:{ details.email}</li>
                        </ul>   
                            </span>
                     
                            {tickets.map(renderTicket)}
                            </tbody>
 
                             </ReactBootStrap.Table>
                            
                            
                       
                    </div>
                      
                </div>
            </div>
                    </div>
        </div>
    );
}
