    // }

    return (
        <div className="ss">
            <div className="row">
                <div className="columnb">
                    <div className="plane">
                        {/* <form >
                          
                                     
                        </form> */}
                    </div>
                </div>
                <div className="column2">
                    <div className="seatInfo">
                        
                        <div>
                            <button style={{marginLeft: "0px",width:"100%",height:"60px"}} className="btn btn-info seatBT">
                              View Opened Tickets
                            </button>
                            
                              
                             
                            <ReactBootStrap.Table  >
                                <br></br>
                            <tbody class="info"> <span class="extra-info">
                            {renderTicket(tickets)}
                            Passenger info
                        <ul>
                            <li>Name:</li>
                            <li>Seat ID</li>
                        </ul>   
                            </span>
                             
                           
                           
                            </tbody>
                                     
                                     
                               
                             </ReactBootStrap.Table>
                            
                       
                    </div>
                      
                </div>
            </div>
                    </div>
        </div>
    );
}
