import React, { useEffect, useState } from 'react'
import axios from 'axios'
import './ViewClosed.css'
import * as ReactBootStrap from 'react-bootstrap';
export default function ViewClosed(props) {
    const [tickets ,setTickets]=useState([])
    const [details, setDetail]= useState([false])

   useEffect(()=> {
        axios.get('http://localhost:8080/bus/ticket/viewClosed').then((res)=>{
        setTickets(res.data);
         
        },[]);
    })
        const renderTicket = (ticket,index) => {   
            var id= JSON.stringify(ticket._id)  ;          
              
            axios.get(`http://localhost:8080/bus/pass/${id}`).then((res)=>{
                    setDetail(res.data);
                   // console.log(details)    
                },[details])    
            return(
                <tr className="btn btn-info " key={index} > 
                    <td style={{backgroundColor:"darkblue", borderColor:"darkblue",width:"40px"}}>{ticket.seatID}</td>
                </tr>
            )           
        }
        

    return (
        <div className="ss">
            <div className="row">
                <div className="columnb">
                    <div className="plane">
                        {/* <form >
                           
                        </form> */}
                    </div>
                </div>
                <div className="column2">
                    <div className="seatInfo">
                        
                        <div>
                            <button  style={{marginLeft: "0px",width:"100%",height:"60px"}} className="btn btn-info seatBT">
                              View Opened Tickets 
                            </button>
                            <br></br>
                            <ReactBootStrap.Table  >
                              <br></br>                              
                            <tbody class="info" > <span class="extra-info">
                         {
                             <tr className="seatInfo "  > 
                             <td style={{backgroundColor:"darkblue", borderColor:"darkblue",width:"40px"}}>{details.seatID}</td>
                         </tr>
                         }
                        <ul >
                            <li>Name:{details.name}</li>
                            <li>Age : { details.age}</li>
                           
                            <li>Email ID:{ details.email}</li>
                        </ul>   
                            </span>
                            <a href='/Admin/pass/'>                         
                            {/* <button  style={{marginLeft: "0px",width:"100%",height:"60px"}} className="btn btn-info seatBT">
                              View pass
                            </button> */}
                            </a>
                            {tickets.map(renderTicket)}
                            </tbody>
 
                             </ReactBootStrap.Table>
  
                             </div>                     
                        </div>
                   </div>
             </div>
        </div>
    );
}
